import { describe, it, expect } from '@jest/globals';
import { getPhotoSize } from '../getPhotoSize';
describe('getPhotoSize', function () {
  it('recognizes an invalid or empty array', function () {
    expect(getPhotoSize(1, 1)).toBeNull();
    expect(getPhotoSize(null, 1)).toBeNull();
    expect(getPhotoSize({}, 1)).toBeNull();
    expect(getPhotoSize([], 1)).toBeNull();
  });
  it('handles src/url differences', function () {
    var url = 'https://vk.com';
    var sizes = [{
      width: 200,
      height: 200,
      src: url
    }, {
      width: 400,
      height: 400,
      url: url
    }, {
      width: 600,
      height: 600
    }];
    expect(getPhotoSize(sizes, 200)).toEqual({
      width: 200,
      height: 200,
      url: url
    });
    expect(getPhotoSize(sizes, 400)).toEqual({
      width: 400,
      height: 400,
      url: url
    });
    expect(getPhotoSize(sizes, 600)).toEqual({
      width: 600,
      height: 600,
      url: ''
    });
  });
  it('returns the minimum achievable image width', function () {
    var photoSizes = [{
      width: 550,
      height: 550
    }, {
      width: 750,
      height: 750
    }, {
      width: 50,
      height: 50
    }];
    expect(getPhotoSize(photoSizes, 500)).toEqual({
      width: 550,
      height: 550,
      url: ''
    });
  });
  it('returns the maximum achievable image width', function () {
    var photoSizes = [{
      width: 450,
      height: 550
    }, {
      width: 350,
      height: 750
    }, {
      width: 50,
      height: 50
    }, {
      width: 240,
      height: 360
    }];
    expect(getPhotoSize(photoSizes, 500)).toEqual({
      width: 450,
      height: 550,
      url: ''
    });
  });
  it('returns the minimum achievable image by width and height', function () {
    var photoSizes = [{
      width: 550,
      height: 550
    }, {
      width: 750,
      height: 750
    }, {
      width: 50,
      height: 50
    }];
    expect(getPhotoSize(photoSizes, 500, 600)).toEqual({
      width: 750,
      height: 750,
      url: ''
    });
  });
  it('returns the maximum achievable image by width and height', function () {
    var photoSizes = [{
      width: 350,
      height: 250
    }, {
      width: 450,
      height: 550
    }, {
      width: 50,
      height: 50
    }];
    expect(getPhotoSize(photoSizes, 500, 600)).toEqual({
      width: 450,
      height: 550,
      url: ''
    });
  });
  it('returns the maximum achievable image in height among the same width', function () {
    var photoSizes = [{
      width: 350,
      height: 250
    }, {
      width: 350,
      height: 550
    }, {
      width: 350,
      height: 300
    }];
    expect(getPhotoSize(photoSizes, 500, 600)).toEqual({
      width: 350,
      height: 550,
      url: ''
    });
  });
  it('returns the maximum size (Infinity hack)', function () {
    var photoSizes = [{
      width: 550,
      height: 550
    }, {
      width: 750,
      height: 750
    }];
    expect(getPhotoSize(photoSizes, Infinity)).toEqual({
      width: 750,
      height: 750,
      url: ''
    });
  });
});
